  image(pic, 0, 0, w, h);

  // flip over vertical axis
  push();
  translate(width, 0);
  scale(-1, 1);
  image(pic, 0, 0, w, h);
  pop();

  // flip over horizontal axis
  push();
  translate(0, height);
  scale(1, -1);
  image(pic, 0, 0, w, h);
  pop();

  // flip over horizontal and vertical axes
  push();
  translate(width, height);
  scale(-1, -1);
  image(pic, 0, 0, w, h);
  pop();
}

