    this.blockColor(this.x, width+10, this.y, this.h, this.c1, this.c2, this.c3);
  }

  move() {
    if (this.x > width || this.x < 0) {
      this.d *= -1;
    }
    this.x += this.d;
  }

  gradientBlock(min, max, y, h, c1, c2) {
    for (let i = min; i <= max; i++) {
      let amt = map(i, min, max, 0, 1);
      let c3 = lerpColor(c1, c2, amt);
      stroke(c3);
      line(i, y, i, y + h);
    }
  }

  blockColor(min, max, y, h, c1, c2, c3) {
    let x = 0.5;
    let range = max - min;
    this.gradientBlock(min, min + x * range, y, h, c1, c2);
    this.gradientBlock(min + x * range, min + max, y, h, c2, c3);
  }
}

let cols, rows;
let size = 13;
let font;
let threshold = .5;
let angle = 0;

function preload() {
  font = loadFont("ibm.ttf");
}

function setup() {
  createCanvas(windowWidth, windowHeight);
  cols = width / size;
  rows = height / size;
  textSize(size);
  textAlign(CENTER, CENTER);
}

function draw() {
  background(0);
  angle += .05; 

  for (let i = 0; i < cols; i++) {
    for (let j = 0; j < rows; j++) {
      let x = size / 2 + i * size;
      let y = size / 2 + j * size;
      let d = dist(x, y, width / 2, height / 2);
      let path = sin(d - angle)
      fill(155);
      textFont(font);
      if (path > threshold) {
        text("0", x, y);
      } else {
        text(".", x, y);
      }
    }
  }
}

function windowResized() {
  resizeCanvas(windowWidth, windowHeight);
}

